import React from "react";
import { Dimensions, StyleSheet, View } from "react-native";
import { useValue, onScrollEvent, interpolateColor } from "react-native-redash/lib/module/v1";
import Animated from "react-native-reanimated";

import Slide, { SLIDE_HEIGHT } from "./Slider";

const BORDER_RADUIS = 75;
const { width } = Dimensions.get("window");

const styles = StyleSheet.create({
    container: {
        flex: 1,
        backgroundColor: "white",
    },
    slider : {
        height: SLIDE_HEIGHT,
        borderBottomRightRadius: BORDER_RADUIS,
    },
    footer: {
        flex: 1,
    },
    footerContent:{
        flex: 1,
        backgroundColor: "white",
        borderTopLeftRadius: BORDER_RADUIS,
    },
});

const slides = [
    { label: "Relaxed", color: "#FFFFF" },
    { label: "Playful", color: "#BEECC4" },
    { label: "Excentric", color: "#FFE4D9" },
    { label: "Funky", color: "#FFDDDD"}  ,
];

const ComponentName = () => {
    const x = useValue(0);
        //TODO:  ScrollHandler useScrollHandler?
    const onScroll = onScrollEvent({ x });
    const backgroundColor = interpolateColor (x, {
        inputRange: slides.map((_, i) => i * width),
        outputRange: slides.map(slides => slides.color),
    });
    return (
        <View style={styles.container}>
            <Animated.View style={[styles.slider, { backgroundColor }]}>
                <Animated.ScrollView horizontal
                  snapToInterval={width}
                  decelerationRate="fast"
                  showsHorizontalScrollIndicator={false}
                  bounces={false}
                  scrollEventThrottle={1}
                  {...{ onScroll }}
                >
                    {slides.map(({ label }, index) => (
                        <Slide key={index} right={!!(index % 2)} {...{ label }} />
                    ))}
                </Animated.ScrollView>   
            </Animated.View>
            <View style={styles.footer}>
                <Animated.View
                  style={{ ...StyleSheet.absoluteFillObject, backgroundColor }}
                />
                <View style={styles.footerContent}>

                </View>
            </View>
        </View>
    );
};

export default ComponentName;
